list.expr:
(
  changes(process_start_time_seconds{job="synapse"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
) * on (instance, job, index) group_left(version) synapse_build_info{job="synapse"}

targets.expr:
(
	rate(python_twisted_reactor_tick_time_sum{index=~"$index",job=~"$job"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
	synapse_server_name_info{server_name="$server_name"}
) / (
	rate(python_twisted_reactor_tick_time_count{index=~"$index",job=~"$job"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
	synapse_server_name_info{server_name="$server_name"}
)

sum by (server_name) (
  process_resident_memory_bytes{job=~"$job",index=~"$index"} * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
)

(
  rate(python_gc_time_sum{job=~"$job",index=~"$index"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
) / (
  rate(python_gc_time_count{job=~"$job",index=~"$index"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
)

(
  rate(python_gc_unreachable_total{job=~"$job",index=~"$index"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
) / (
  rate(python_gc_time_count{job=~"$job",index=~"$index"}[$bucket_size]) * on (instance, job, index) group_left(server_name)
  synapse_server_name_info{server_name="$server_name"}
)
